transform: 			scale(0.5);
-webkit-transition: all 0.6s ease;
-moz-transition: 	all 0.6s ease;
transition: 		all 0.6s ease;
-webkit-transform-origin: 0% 0%;
-moz-transform-origin: 0% 0%;
-ms-transform-origin: 0% 0%;
transform-origin: 0% 0%;
