					}
					else
					{
						menu.addClass('av-open-submenu');
					}
			};

		win.on("debouncedresize av-height-change",  calc_margin ); calc_margin();

		if(setWitdth.length)
		{
			win.on("debouncedresize av-height-change",  calc_values );
