.mejs-container {
	position: relative;
	min-width:150px;
	background: none;
	font-family: sans-serif;
	text-align: left;
	vertical-align: top;
	width:100% !important;

}


.me-plugin {
	position: absolute;
}

.mejs-embed, .mejs-embed body {
	width: 100%;
	height: 100%;
	margin: 0;
	padding: 0;
	background: #000;
	overflow: hidden;
}

.mejs-container-fullscreen {
	position: fixed;
	left: 0;
	top: 0;
	right: 0;
	bottom: 0;
	overflow: hidden;
	z-index: 1000;
}
.mejs-container-fullscreen .mejs-mediaelement,
.mejs-container-fullscreen video {
	width: 100%;
	height: 100%;
}

.mejs-container.mejs-audio{ border-radius: 3px; }


/* Start: LAYERS */
.mejs-background {
	position: absolute;
	top: 0;
	left: 0;
}
.mejs-mediaelement {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
}
.mejs-poster {
	position: absolute;
	top: 0;
	left: 0;
	overflow:hidden;
}
.mejs-poster img {
	border: 0;
	padding: 0;
	border: 0;
	display: block;
	width: auto;
	height: 100%;
}
.mejs-overlay {
	position: absolute;
	top: 0;
	left: 0;
	width: 100% !important;
	height: 100% !important;
}
.mejs-overlay-play {
	cursor: pointer;
	width:100% !important;
	height:100% !important;
}

div .mejs-overlay-button {
	position: absolute;
	top: 50%;
	left: 50%;
	width: 100px;
	height: 100px;
	margin: -50px 0 0 -50px !important;
}





.mejs-overlay-loading {
	position: absolute;
	top: 50%;
	left: 50%;
	width: 80px;
	height: 80px;
	margin: -40px 0 0 -40px;
}
/*
div .mejs-overlay-loading span {
	display:block;
	width: 80px;
	height: 80px;
	background: #3A3C3D url(loading.gif) 50% 50% no-repeat;
	border-radius: 40px;
	z-index: 10000;
}
*/

/* End: LAYERS */

/* Start: CONTROL BAR */
div .mejs-container .mejs-controls {
	position: absolute;
	margin: 0;
	padding: 0;
	bottom: 0;
	left: 0;
	height: 30px;
	width: 100%;
	background: #3a3c3d url(background.png);
	background: -webkit-linear-gradient(#4a4c4d, #2b2d2d); 
	background: -moz-linear-gradient(#4a4c4d, #2b2d2d);
	background: linear-gradient(#4a4c4d, #2b2d2d); 	
	list-style: none;
	white-space: nowrap;
}

.mejs-audio .mejs-controls{
	border-radius:3px;
}

div .mejs-container .mejs-controls  div {
	list-style-type: none;
	display: inline-block;
	margin: 0;
	padding: 0;
	width: 32px;
	height: 30px;
	font-size: 11px;
	line-height: 11px;
	font-family: sans-serif;
	background: url(sep.png) left no-repeat;
	border: 0;
}

div .mejs-controls .mejs-button button {
	cursor: pointer;
	display: block;
	font-size: 0;
	line-height: 0;
	text-decoration: none;
	margin: 7px 7px 0 9px;
	padding: 0;
	position: absolute;
	height: 16px;
	width: 15px;
	border: 0;
	background: transparent url(controls.png) no-repeat;
}

/* :focus for accessibility */
body .mejs-controls .mejs-button button:focus {
	outline:none
}

/* End: CONTROL BAR */

/* Start: Time (current / duration) */
body .mejs-container .mejs-controls .mejs-time {
	color: #fff;
	display: inline-block;
	height: 17px;
	width: auto;
	padding: 8px 8px 5px 10px;
	overflow: hidden;
	text-align: center;
	box-sizing: content-box;
	-moz-box-sizing: content-box;
	-webkit-box-sizing: content-box;
}
body .mejs-container .mejs-controls .mejs-time span {
	font-size: 11px;
	color: #ddd;
	line-height: 12px;
	display: block;
	float: left;
	width: auto;
	margin:1px 2px 0 2px;
}
/* End: Time (current / duration) */


/* Start: Play/pause */
body .mejs-container .mejs-controls .mejs-playpause-button {
	cursor: pointer;
	background: none;
	width: 30px;
}
body .mejs-controls .mejs-playpause-button button {
	margin:7px 8px;
}
body .mejs-controls .mejs-play button {
	background-position:0 0;
}
body .mejs-controls .mejs-play:hover button {
	background-position:0 -32px;
}
body .mejs-controls .mejs-pause button {
	background-position:0 -16px;
}
body .mejs-controls .mejs-pause:hover button {
	background-position:0 -48px;
}
/* End: Play/pause */


/* Stop */
body .mejs-controls .mejs-stop button {
	background-position: -112px 0;
}
body .mejs-controls .mejs-stop:hover button {
	background-position: -112px -32px;
}
/* End: Play/pause */

/* Start: Progress bar */


body .mejs-controls div.mejs-time-rail {
	width: 200px;
}

body .mejs-controls .mejs-time-rail span {
	display: block;
	position: absolute;
	width: 180px;
	height: 5px;
	cursor: pointer;
}
body .mejs-controls .mejs-time-rail .mejs-time-total {
	padding: 1px;
	margin: 11px 8px 0 10px;
	background: #262929;
	border-bottom: #3d3f40 1px solid;
	box-shadow: inset 1px 1px rgba(0, 0, 0, .4);
}
body .mejs-controls .mejs-time-rail .mejs-time-buffering {
	width:100%;
	background:#464849;
	background-image: -webkit-linear-gradient(-45deg, #464849 25%, #262929 25%, #262929 50%, #464849 50%, #464849 75%, #262929 75%, #262929);
	background-image: -moz-linear-gradient(-45deg, #464849 25%, #262929 25%, #262929 50%, #464849 50%, #464849 75%, #262929 75%, #262929);
	background-image: linear-gradient(-45deg, #464849 25%, #262929 25%, #262929 50%, #464849 50%, #464849 75%, #262929 75%, #262929);
	background-size: 10px 10px;
	-webkit-animation: buffering-stripes 2s linear infinite;
	-moz-animation: buffering-stripes 2s linear infinite;
	animation: buffering-stripes 2s linear infinite;
	z-index: 1;
}

@-webkit-keyframes buffering-stripes { from {background-position: 0 0;} to {background-position: 30px 0;} }
@-moz-keyframes buffering-stripes { from {background-position: 0 0;} to {background-position: 30px 0;} }
@keyframes buffering-stripes { from {background-position: 0 0;} to {background-position: 30px 0;} }

body .mejs-controls .mejs-time-rail .mejs-time-loaded {
	background: #464849;
	width: 0;
}
body .mejs-controls .mejs-time-rail .mejs-time-current {
	width: 0;
	background: #feea36;
	background-image: -webkit-linear-gradient(-45deg, #feea36 25%, #fdd500 25%, #fdd500 50%, #feea36 50%, #feea36 75%, #fdd500 75%, #fdd500);
	background-image: -moz-linear-gradient(-45deg, #feea36 25%, #fdd500 25%, #fdd500 50%, #feea36 50%, #feea36 75%, #fdd500 75%, #fdd500);
	background-image: linear-gradient(-45deg, #feea36 25%, #fdd500 25%, #fdd500 50%, #feea36 50%, #feea36 75%, #fdd500 75%, #fdd500);
	background-size: 6px 6px;
	z-index: 1;

}

body .mejs-controls .mejs-time-rail .mejs-time-handle {
	display: none;
	position: absolute;
	margin: 0;
	width: 5px;
	background: transparent;
	cursor: pointer;
	top: 1px;
	z-index: 1;
	text-align: center;
}

body .mejs-controls .mejs-time-rail .mejs-time-float {
	position: absolute;
	display: none;
	background: #C2C8CF;
	background: -webkit-linear-gradient(#E3E6E9, #C2C8CF);
	background: -moz-linear-gradient(#E3E6E9, #C2C8CF);
	background: linear-gradient(#E3E6E9, #C2C8CF);
	width: 36px;
	height: 17px;
	border-radius: 2px;
	box-shadow:0 1px 1px rgba(0, 0, 0, .4);
	top: -26px;
	margin-left: -18px;
	text-align: center;
	color: #111;
	border:none;
}

body .mejs-controls .mejs-time-rail .mejs-time-float-current {
	margin: 3px;
	width: 30px;
	display: block;
	text-align: center;
	left: 0;
}
body .mejs-controls .mejs-time-rail .mejs-time-float-corner {
	position: absolute;
	display: block;
	width: 0;
	height: 0;
	line-height: 0;
	border: solid 4px #C2C8CF;
	border-color: #C2C8CF transparent transparent transparent;
	border-radius: 0;
	top: 17px;
	left: 13px;

}




