            if(self.sortDirection() == 'asc' && self.selectedSortType() == 'title'){
                locations.sort(asc_bytitle);
            }
            else if(self.sortDirection() == 'dec' && self.selectedSortType() == 'title'){
                locations.sort(dec_bytitle);
            }
