                        });

                        //Marker click
                        //-----------------------------
                        google.maps.event.addListener(marker, 'click', function () {
                            //Show the bubble
                            viewModel.showInfoWindow(location);
                            //Hide the item on
                            location.expanded(!location.expanded());
                        });

                        //Set the marker
                        //-----------------------------u
                        location._mapMarker = marker;
                        viewModel.markers.push(marker);
